import React, { useEffect } from "react";
import logo from "../../img/logo.svg";
import { useNavigate } from "react-router-dom";
import "./Form.scss";
function Form({
  people,
  setpeople,
  personDetails,
  setpersonDetails,
  editPeople,
  setEditPeople,
}) {
  const onChangeHandler = (e) =>{
    const{name, value} = e.target;
  return setpersonDetails({
    ...personDetails,
    id:new Date().getMilliseconds(),
    [name]:value
    })
  }
  const onUpdatePerson = (personDetails, ID) => {
    const editedPerson = people.map((person) => {
      return person.ID === ID ? { ...personDetails } : person;
    });
    setpeople(editedPerson);
    setEditPeople("");
  };
//   useEffect(() =>{
// console.log(personDetails);
//   },[personDetails])
  useEffect(() => {

    if (editPeople) {
      setpersonDetails({ ...editPeople });
    } else {
      setpersonDetails("");
    }
  }, [setpersonDetails, editPeople]);
  let navigate = useNavigate();
  const onSubmitForm = (e) => {
    e.preventDefault();
    if (!editPeople) {
      setpeople([{ ID: new Date(), ...personDetails }, ...people]);
      setpersonDetails({
        username: "",
        email: "",
        password: "",
      });
    } else {
      onUpdatePerson(personDetails, editPeople.ID);
    }
    navigate("/list");
  };
  useEffect(() => {
    console.log(people);
  }, [people]);

  // useEffect(() =>{
  //     console.log(people)
  // }, [people])

  return (
    <div className="form-box">
      <form onSubmit={onSubmitForm}>
        <h1>{editPeople ? "UPDATE" : "SIGN UP"}</h1>
        <div className="icon">
          <img src={logo} alt="logo-svg" />
        </div>
        <div className="formcontainer">
          <div className="container">
            <label htmlFor="uname">
              <strong>Username</strong>
            </label>
            <input
              type="text"
              placeholder="Enter Username"
              name="username"
              value={personDetails.username}
              onChange={onChangeHandler}
              // onChange={(e) => {
              //   setpersonDetails({
              //     ...personDetails,
              //     username: e.target.value,
              //   });
              // }}
              required
            />
            <label htmlFor="mail">
              <strong>E-mail</strong>
            </label>
            <input
              type="email"
              placeholder="Enter E-mail"
              name="email"
              value={personDetails.email}
              onChange={onChangeHandler}
              // onChange={(e) => {
              //   setpersonDetails({
              //     ...personDetails,
              //     email: e.target.value,
              //   });
              // }}
              required
            />
            <label htmlFor="psw">
              <strong>Password</strong>
            </label>
            <input
              type="password"
              placeholder="Enter Password"
              name="password"
              value={personDetails.password}
              onChange={onChangeHandler}
              // onChange={(e) => {
              //   setpersonDetails({
              //     ...personDetails,
              //     password: e.target.value,
              //   });
              // }}
              required
            />
          </div>
          <button type="submit">
            <strong>{editPeople ? "Update" : "sign up"}</strong>
          </button>
          <div className="container">
            <label>
              <input type="checkbox" checked="checked" name="remember" />{" "}
              Remember me
            </label>
            <span className="psw">
              <a href="#">Forgot password?</a>
            </span>
          </div>
        </div>
      </form>
    </div>
  );
}

export default Form;
